
-- Create timezones table with comprehensive timezone data
CREATE TABLE timezones (
    id UUID PRIMARY KEY DEFAULT gen_random_uuid(),
    value TEXT UNIQUE NOT NULL,
    abbr TEXT NOT NULL,
    utc_offset NUMERIC NOT NULL,
    isdst BOOLEAN NOT NULL,
    text TEXT NOT NULL,
    utc TEXT[] NOT NULL,
    created_at TIMESTAMP WITH TIME ZONE DEFAULT now(),
    updated_at TIMESTAMP WITH TIME ZONE DEFAULT now()
);

-- Create indexes for efficient querying
CREATE INDEX idx_timezones_value ON timezones(value);
CREATE INDEX idx_timezones_abbr ON timezones(abbr);
CREATE INDEX idx_timezones_utc_offset ON timezones(utc_offset);
CREATE INDEX idx_timezones_text ON timezones(text);

-- Insert timezone data

-- Enable RLS on timezones table (reference data should be publicly readable)
ALTER TABLE timezones ENABLE ROW LEVEL SECURITY;

-- Create policy to allow everyone to read timezones (reference data)
CREATE POLICY "Timezones are publicly readable" 
  ON timezones 
  FOR SELECT 
  TO public
  USING (true);

-- Create trigger to update updated_at timestamp
CREATE TRIGGER timezones_updated_at
  BEFORE UPDATE ON timezones
  FOR EACH ROW
  EXECUTE FUNCTION update_updated_at_column();
